import React, { useState } from "react";
import { delay, motion } from "framer-motion"; 
import './contact.css';

export default function Contact() {
  const [expandedBox, setExpandedBox] = useState(null);

  const toggleExpand = (boxId) => {
    setExpandedBox(expandedBox === boxId ? null : boxId); // Toggle expansion
  };
  return (
    <section className="contact-section">
        <h2 className="contact-heading">Hit Me Up!</h2>
            <div className="contact-content">
                <div className="myinfo">
                <div id="infobox">
                {[
                { id: 1, title: "Experience", content: "I’m still learning but have a passion for creating clean and functional designs." },
                { id: 2, title: "Projects", content: "I’ve worked on 2+ projects, showcasing responsive and interactive designs." },
                { id: 3, title: "Support", content: "I’m always available to collaborate and share ideas, providing 24/7 support." },
                ].map((box) => (
                <motion.div
                    key={box.id}
                    id="infobox_item"
                    onClick={() => toggleExpand(box.id)}
                    layout
                    animate={{
                    height: expandedBox === box.id ? "16rem" : "8rem",
                    width: expandedBox === box.id ? "18rem" : "9rem",
                    }}
                    transition={{duration: 0.4 }}
                    className={`box ${expandedBox === box.id ? "hovered" : ""}`}
                >
                    <motion.span
                    layout
                    className="boxtitle"
                    animate={{
                        fontSize: expandedBox === box.id ? "1.5rem" : "1rem",
                    }}
                    transition={{ delay:0.3,duration: 0.3}}
                    >
                    {box.title}
                    </motion.span>
                    {expandedBox === box.id && (
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        transition={{ delay:0.6,duration: 0.3 }}
                        className="expandedContent"
                    >
                        {box.content}
                    </motion.div>
                    )}
                </motion.div>
                ))}
                </div>
            
            <div className="sendproject">
                <h2 className='sendproject-heading'>Write me up your project</h2>
                <input className='input-field' type="text" placeholder={"Enter your name"} />
                <input className='input-field' type="text" placeholder={"Enter your email"} />
                <input className='input-field-box' type="text" placeholder={"Write your project"} />
            </div>
            </div>
        </div>
    </section>
  )
}
